    /**
     * 11. Container With Most Water
     */
    @Test
    public void maxAreaTest() {
        int[] height = {1, 8, 6, 2, 5, 4, 8, 3, 7};
        Assert.assertEquals(49, iam.maxArea(height));
        int[] h = {8, 7, 2, 1};
        Assert.assertEquals(7, iam.maxArea(h));
    }

