    /**16. 3Sum Closest*/
    @Test
    public void threeSumClosestTest(){
         int[] nums = {-1,2,1,-4};
         Assert.assertEquals(2,iam.threeSumClosest(nums,1));
    }

    @Test
    public void threeSumClosestXTest(){
        int[] nums = {-1,2,1,-4};
        Assert.assertEquals(2,iam.threeSumClosestX(nums,1));
    }
    /**18. 4Sum*/
    @Test
    public void fourSumTest(){
        //The maximum value of a 32-bit signed integer is \(2147483647\). The minimum value of a 32-bit signed integer is \(-2147483648\).
        int[] nums = {1000000000,1000000000,1000000000,1000000000};
        Assert.assertEquals(0,iam.fourSum(nums,-294967296).size());
    }
    /**31. Next Permutation*/
    @Test
    public void nextPermutationTest(){
        int[] nums = {2,3,1};
        iam.nextPermutation(nums);
        System.out.print(Arrays.toString(nums));
    }
    /**33. Search in Rotated Sorted Array */
    @Test
    public void searchTest(){
        int[] nums={4,5,6,7,0,1,2};
        Assert.assertEquals(4,iam.search(nums,0));
        int[] nums1 ={3,1};
        Assert.assertEquals(1, iam.search(nums1,1));
    }

    /**34. Find First and Last Position of Element in Sorted Array**/
    @Test
    public void searchRangeTest(){
       int [] nums = {5,7,7,8,8,10};
       int target = 8;
       int []rs = iam.searchRange(nums,target);
        Assert.assertEquals(3, rs[0]);
        Assert.assertEquals(4, rs[1]);

        int [] nums1 = {5,7,7,8,8,10};
        int target1 = 6;
        int []rs1 = iam.searchRange(nums1,target1);
        Assert.assertEquals(-1, rs1[0]);
        Assert.assertEquals(-1, rs1[1]);
    }

